import { createStore } from "redux";

import { createSlice, configureStore } from "@reduxjs/toolkit";
//cartIsvisible
const initialisShownState = { isshow: false };
const isshownSlice = createSlice({
  name: "isshownSlice",
  initialState: initialisShownState,
  reducers: {
    toggle(state) {
      state.isshow = !state.isshow;
    },
  },
});
//cartSLice
const cartSlice = createSlice({
  name: "cartSlice",
  initialState: { items: [], totalQuantit },
  reducers: {},
});
const addtoCartState = { item: "", price: "" };
const store = configureStore({
  reducer: { isshown: isshownSlice.reducer },
});

export const isshownActions = isshownSlice.actions;

export default store;
