import { createStore } from "redux";

import { createSlice, configureStore } from "@reduxjs/toolkit";
const initialisShownState = { isshow: false };
const isshownSlice = createSlice({
  name: "isshownSlice",
  initialState: initialisShownState,
  reducers: {
    isShow(state) {
      state.isshow = true;
    },
    notShow(state) {
      state.isshow = false;
    },
  },
});

const store = configureStore({
  reducer: { isshown: isshownSlice.reducer, auth: authSlice.reducer },
}); //put an object in configureStore

export const counterActions = isshownSlice.actions;

export default store;
